import React,{useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
 const [movies,setmovies] =useState([]);
 const [loading,setloading] =useState(false);
 const [err,seterr]=useState(null);

  let dummyMovies={};

  async function movieHandler()
  { 
    try{
      setloading(true);
    const response=await fetch('https://swapi.dev/api/film/')
    if(!response.ok){
      throw new Error('Something went wrong!');
    }
    const data=await response.json()

    dummyMovies = data.results.map(Element=>{
                  return {id:Element.episode_id,
                          title:Element.title,
                          openingText:Element.opening_crawl,
                          releaseDate:Element.release_date}})
    setmovies(dummyMovies);
    setloading(false);
    }
    catch(error){
      seterr(error);
    }


  }

  return (
    <React.Fragment>
      <section>
        <button onClick={movieHandler}>Fetch Movies</button>
      </section>
      <section>
        {!loading && movies.length==0 && !err && <p>No moives found</p> }
        {!loading && movies.length>0 && <MoviesList movies={movies} />}
        {loading && err && <p>{err}</p>}
        {loading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
