import React,{useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
 const [movies,setmovies] =useState([]);
  let dummyMovies={};
  function movieHandler(){
  fetch('https://swapi.dev/api/films/').then((response)=>{ return response.json()})
  .then((data)=>
  {
  dummyMovies = data.results.map(Element=>{
                return {id:Element.episode_id,
                        title:Element.title,
                        openingText:Element.opaning_crawl,
                        releaseDate:Element.release_date}})
  return setmovies(dummyMovies);

  })
}

  return (
    <React.Fragment>
      <section>
        <button onClick={movieHandler}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={dummyMovies} />
      </section>
    </React.Fragment>
  );
}

export default App;
